                overflow: hidden;
                position: relative;
    
                .photoBox{
                    display: flex;
                    position: absolute;
                    left: -200px;
    
                    img{
                        width: 200px; height: 200px;
                        object-fit: cover; border-radius: 20px;
                        display: block;
                    }
                }
            }
        }
        


        .profileMsg{
            width: 525px; height: 100%;
            display: flex; align-items: center; justify-content: center;
            color: white;
            font-size: 36px; line-height: 60px;
            letter-spacing: -4px;
            font-weight: bold;
            text-align: center;
